package com.google.page;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 4;
		switch(i)
		{
		case 1:
			System.out.println("one");
		case 2:
			System.out.println("two");
		default:
			System.out.println("choose one or two");
		    break;
		}

	}

}
